{"datetime_as_string":{"scriptActive":true,"script":"var subject = fd_data.trigger.subject.toString();
var regex = /\w* \d{1,2} \w* \d{4}, \d:\d{2} A?P?M/g;
var dateFromString = subject.match(regex);
return dateFromString[0];"}}

{"glidedatetime":{"scriptActive":true,"script":"var subject = fd_data.trigger.subject.toString();
var regex = /\w* \d{1,2} \w* \d{4}, \d:\d{2} A?P?M/g;
var dateFromString = subject.match(regex);
var newDateString = dateFromString[0];

function convertDateToServiceNowFormat(dateString) {
 var months = {
 'January': '01',
 'February': '02',
 'March': '03',
 'April': '04',
 'May': '05',
 'June': '06',
 'July': '07',
 'August': '08',
 'September': '09',
 'October': '10',
 'November': '11',
 'December': '12'
 };

 var dateParts = dateString.match(/(\w+) (\d+) (\w+) (\d{4}), (\d+):(\d+) (\w+)/);

 var dayOfWeek = dateParts[1]; // not needed for ServiceNow format
 var day = String("0" + dateParts[2]).slice(-2);
 var monthName = dateParts[3];
 var month = months[monthName];
 var year = dateParts[4];
 var hour12 = parseInt(dateParts[5], 10);
 var minute = dateParts[6];
 var amPm = dateParts[7];

 var hour24 = amPm === 'PM' ? (hour12 === 12 ? hour12 : hour12 + 12) : (hour12 === 12 ? 0 : hour12);

 return year + '-' + month + '-' + day + ' ' + String("0" + hour24).slice(-2) + ':' + minute + ':00';
}

var serviceNowDate = convertDateToServiceNowFormat(newDateString);
return serviceNowDate;"}}

